CREATE OR REPLACE FUNCTION obter_txt_quest_click_panorama ( cd_icone_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE

/* Function utilizada no objeto 352648 (dic_objeto), e retorna o local do ícone padrão contido no java, no projeto BedsOutlook / Department.java */

nr_seq_regra_w		bigint;
ds_retorno_w 		varchar(255);


BEGIN

select	max(a.nr_sequencia)
into STRICT 	nr_seq_regra_w
from	regra_perm_panorama a
where	a.cd_perfil = cd_perfil_p
and exists (	SELECT	1
		from	regra_panorama_icone_click x
		where	x.nr_seq_regra = a.nr_sequencia);

if (coalesce(nr_seq_regra_w::text, '') = '') then -- se não encontrou a regra para o perfil, busca uma regra sem perfil definido.
	select	max(a.nr_sequencia)
	into STRICT 	nr_seq_regra_w
	from	regra_perm_panorama a
	where	coalesce(a.cd_perfil::text, '') = ''
	and exists (	SELECT	1
			from	regra_panorama_icone_click x
			where	x.nr_seq_regra = a.nr_sequencia);
end if;

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	select	max(ds_texto_questionamento)
	into STRICT	ds_retorno_w
	from	regra_panorama_icone_click
	where	nr_seq_regra = nr_seq_regra_w
	and	cd_icone_panorama = cd_icone_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_txt_quest_click_panorama ( cd_icone_p bigint, cd_perfil_p bigint) FROM PUBLIC;

