CREATE OR REPLACE FUNCTION obter_tx_desconto_terceiro (nr_repasse_terceiro_p bigint) RETURNS bigint AS $body$
DECLARE

dt_referencia_w	timestamp;
vl_retorno_w	double precision;


BEGIN

select	dt_mesano_referencia
into STRICT	dt_referencia_w
from	repasse_terceiro
where	nr_repasse_terceiro	= nr_repasse_terceiro_p;

select	coalesce(max(tx_desconto),0) * -1
into STRICT	vl_retorno_w
from	regra_desc_repasse a
where	trunc(a.dt_mesano_ref,'month') = trunc(dt_referencia_w,'month');

return 	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
