CREATE OR REPLACE FUNCTION obter_tx_mort_ocup_setor ( cd_setor_Atendimento_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE

tx_mortalidade_w	 double precision;

BEGIN

select	dividir((sum(NR_LEITOS_OCUPADOS) * 100), (sum(NR_LEITOS_OCUPADOS + NR_LEITOS_LIVRES)))
into STRICT	tx_mortalidade_w
from	eis_ocupacao_setor_v
where	cd_setor_atendimento	= cd_setor_Atendimento_p
and	dt_referencia		between dt_referencia_p and last_day(dt_referencia_p)
