CREATE OR REPLACE FUNCTION obter_uf_pf (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_uf_w			varchar(40);

C01 CURSOR FOR
	SELECT	sg_estado
	from	compl_pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	(ds_municipio IS NOT NULL AND ds_municipio::text <> '')
	order by ie_tipo_complemento desc,
		sg_estado desc;


BEGIN
ds_uf_w		:= 'N/E';
open C01;
loop
fetch C01 into
		ds_uf_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ds_uf_w	:= ds_uf_w;
end loop;
close C01;

return ds_uf_w;

end;
