CREATE OR REPLACE FUNCTION obter_uf_prescrita ( NR_SEQUENCIA_P bigint) RETURNS bigint AS $body$
DECLARE


UF_PRESCRITA_W 	double precision;


BEGIN

SELECT max(p.qt_ultrafiltracao)
INTO STRICT UF_PRESCRITA_W
FROM     prescr_mat_hor m,
	 hd_prescricao p,
	 prescr_medica pr
WHERE m.nr_seq_dialise = nr_sequencia_p
AND m.nr_prescricao = p.nr_prescricao
AND p.nr_prescricao = pr.nr_prescricao
and coalesce(pr.dt_suspensao::text, '') = '';


RETURN UF_PRESCRITA_W;


END;
