CREATE OR REPLACE FUNCTION obter_ultima_aval_subj_detsky ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ie_aval_nut_w		varchar(255);
dt_avaliacao_w		varchar(50);


BEGIN

select	max(a.ie_aval_nut),
		to_char(max(dt_avaliacao),'dd/mm/yyyy hh24:mi:ss')
into STRICT	ie_aval_nut_w ,
		dt_avaliacao_w
from	aval_nutric_subjetiva a
where	nr_atendimento = nr_atendimento_p
and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and 	coalesce(dt_inativacao::text, '') = ''
and		a.nr_sequencia = (	SELECT max(x.nr_sequencia)
	from   aval_nutric_subjetiva x
	where  x.nr_atendimento	= nr_atendimento_p
	and	   (x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
	and	   coalesce(x.dt_inativacao::text, '') = '');

if (ie_opcao_p = 'AV') then
	return	ie_aval_nut_w;
elsif (ie_opcao_p = 'DT') then
	return	dt_avaliacao_w;
end if;

end;
$body$
