SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ultima_dt_alta (nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp;
nr_sequencia_w		bigint;
ie_liberar_desfecho_w	varchar(1);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select  coalesce(max(ie_liberar_desfecho),'N')
	into STRICT	ie_liberar_desfecho_w
	from	parametro_medico
	where	cd_estabelecimento = obter_estabelecimento_ativo;

	select 	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from 	ATENDIMENTO_ALTA a
	where 	a.nr_atendimento = nr_atendimento_p
	and 	a.ie_desfecho = 'A'
	and     ((a.ie_tipo_orientacao <> 'P')
	or (ie_liberar_desfecho_w  = 'N')
	or      ((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') and (coalesce(a.dt_inativacao::text, '') = '')))
	and 	not exists (
		SELECT	1
		from 	atendimento_alta b
		where	b.nr_atendimento = nr_atendimento_p
		and		b.ie_desfecho <> 'A'
		and		b.nr_sequencia > a.nr_sequencia
		and     ((b.ie_tipo_orientacao <> 'P')
		or (ie_liberar_desfecho_w  = 'N')
		or      ((b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '') and (coalesce(b.dt_inativacao::text, '') = '')))
	);

	select 	max(DT_DESFECHO)
	into STRICT	dt_retorno_w
	from	ATENDIMENTO_ALTA
	where 	nr_atendimento = nr_atendimento_p
	and		nr_sequencia = nr_sequencia_w
	and 	ie_desfecho = 'A';

end if;

return	dt_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
