SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ultima_mensagem_carta (nr_seq_carta_p bigint, nr_seq_carta_mae_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	carta_comunicacao.ds_mensagem%type;
nm_usuario_w	varchar(15) := wheb_usuario_pck.get_nm_usuario;


BEGIN
ds_retorno_w := null;

select	max(a.ds_mensagem)
into STRICT	ds_retorno_w
from	carta_comunicacao a
where  	nr_seq_carta in (	SELECT nr_sequencia
							FROM	carta_medica
							WHERE nr_seq_carta_mae = nr_seq_carta_mae_p
							
union all

							SELECT 	coalesce(nr_seq_carta_mae,nr_sequencia)
							FROM	carta_medica
							WHERE nr_sequencia = nr_seq_carta_p)
and    	a.ie_leitura = 'N'
and	coalesce(nm_destinatario,nm_usuario_w) = nm_usuario_w
and 	not exists (	select 	x.nr_sequencia
						from  	carta_comunicacao x
						where 	a.nr_seq_carta = x.nr_seq_carta
						and   	x.dt_atualizacao > a.dt_atualizacao
						and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
						and		coalesce(dt_inativacao::text, '') = ''
						and    	a.IE_LEITURA = 'N')
and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and		coalesce(dt_inativacao::text, '') = ''
order by dt_atualizacao_nrec desc;

if (coalesce(ds_retorno_w::text, '') = '') then
	select	max(a.ds_mensagem)
	into STRICT	ds_retorno_w
	from	carta_comunicacao a
	where  	nr_seq_carta in (	SELECT nr_sequencia
								FROM	carta_medica
								WHERE nr_seq_carta_mae = nr_seq_carta_mae_p
								
union all

								SELECT 	coalesce(nr_seq_carta_mae,nr_sequencia)
								FROM	carta_medica
								WHERE nr_sequencia = nr_seq_carta_p)
	and	coalesce(nm_destinatario,nm_usuario_w) = nm_usuario_w
	and 	not exists (	select 	x.nr_sequencia
							from  	carta_comunicacao x
							where 	a.nr_seq_carta = x.nr_seq_carta
							and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
							and		coalesce(dt_inativacao::text, '') = ''
							and   	x.dt_atualizacao > a.dt_atualizacao)
	and		(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and		coalesce(dt_inativacao::text, '') = ''
	order by dt_atualizacao_nrec desc;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
