CREATE OR REPLACE FUNCTION obter_ultima_ordem_entregue ( cd_estabelecimento_p bigint, cd_material_p bigint, qt_dias_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_ordem_compra_w			bigint;
dt_real_entrega_w				timestamp;

c01 CURSOR FOR 
SELECT	distinct 
	a.nr_ordem_compra, 
	c.dt_real_entrega 
from	ordem_compra_item_entrega c, 
	ordem_compra_item b, 
	ordem_compra a 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	b.nr_ordem_compra = c.nr_ordem_compra 
and	b.nr_item_oci = c.nr_item_oci 
and	a.cd_estabelecimento = cd_estabelecimento_p 
and	b.cd_material = cd_material_p 
and	trunc(c.dt_real_entrega,'dd') between trunc(clock_timestamp(),'dd') - coalesce(qt_dias_p,90) and trunc(clock_timestamp(),'dd') 
and	coalesce(a.nr_seq_motivo_cancel::text, '') = '' 
and	coalesce(c.dt_cancelamento::text, '') = '' 
and	(c.dt_real_entrega IS NOT NULL AND c.dt_real_entrega::text <> '') 
order by	c.dt_real_entrega desc;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_ordem_compra_w, 
	dt_real_entrega_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	nr_ordem_compra_w := nr_ordem_compra_w;
	exit;
	end;
end loop;
close C01;
 
return	nr_ordem_compra_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
