CREATE OR REPLACE FUNCTION obter_ultima_pac_saida_hosp (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

ds_observacao_w		varchar(255) := null;
nr_min_sequencia_w  bigint;

BEGIN
select coalesce(min(nr_sequencia),0)
into STRICT nr_min_sequencia_w
from atend_pac_saida_hosp
where nr_atendimento = nr_atendimento_p
and   coalesce(dt_chegada::text, '') = '';

if (	nr_min_sequencia_w	>	0) then
	select  ds_observacao
	into STRICT ds_observacao_w
	from atend_pac_saida_hosp a
	where	a.nr_atendimento = nr_atendimento_p
	and	coalesce(a.dt_chegada::text, '') = ''
	and	a.nr_sequencia = nr_min_sequencia_w;
end if;


return	ds_observacao_w;

end;
