CREATE OR REPLACE FUNCTION obter_ultima_sessao_agecons ( cd_pessoa_fisica_p bigint, cd_agenda_p bigint, nr_seq_agenda_origem_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_agenda_w 		agenda_consulta.cd_agenda%type;
nr_secao_w				bigint;
qt_total_secao_w		bigint;
ie_conflito_secao_w		bigint;
nr_controle_secao_w		bigint;
ie_cons_agenda_secao_w	varchar(1);
cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;


BEGIN

cd_pessoa_fisica_w := to_char(cd_pessoa_fisica_p);

ie_cons_agenda_secao_w := Obter_Param_Usuario(866, 236, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_cons_agenda_secao_w);

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') then

select	max(nr_controle_secao)
into STRICT	nr_controle_secao_w
from	agenda_consulta
where	nr_sequencia = nr_seq_agenda_origem_p;

if (nr_controle_secao_w IS NOT NULL AND nr_controle_secao_w::text <> '') then
	select 	max(a.nr_sequencia) --ultima sessao que o paciente teve
	into STRICT	nr_seq_agenda_w
	from   	agenda_consulta a
	where  	((a.cd_agenda 	 	= cd_agenda_p) or (ie_cons_agenda_secao_w = 'N'))
	and		a.cd_pessoa_fisica 	= cd_pessoa_fisica_w
	and		(qt_total_secao IS NOT NULL AND qt_total_secao::text <> '')
	and		a.nr_controle_secao = nr_controle_secao_w
	and		a.ie_status_agenda	<> 'C';
else
	select 	max(a.nr_sequencia) --ultima sessao que o paciente teve
	into STRICT	nr_seq_agenda_w
	from   	agenda_consulta a
	where  	((a.cd_agenda 	 	= cd_agenda_p) or (ie_cons_agenda_secao_w = 'N'))
	and		a.cd_pessoa_fisica 	= cd_pessoa_fisica_w
	and		(qt_total_secao IS NOT NULL AND qt_total_secao::text <> '')
	and		a.ie_status_agenda	<> 'C';
end if;

if (nr_seq_agenda_w IS NOT NULL AND nr_seq_agenda_w::text <> '') then
	select 	max(a.nr_secao),
			max(a.qt_total_secao)
	into STRICT	nr_secao_w,
			qt_total_secao_w
	from	agenda_consulta a
	where	a.nr_sequencia	= nr_seq_agenda_w;
end if;

if (nr_secao_w = qt_total_secao_w) then
	return null;
else
	return nr_seq_agenda_w;
end if;

end if;
end;
