CREATE OR REPLACE FUNCTION obter_ultima_spa_aprov_usuario ( nr_seq_spa_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_ultima_aprov_w		varchar(1) := 'N';
nr_seq_aprovacao_w 		spa_aprovacao.nr_sequencia%type;
nm_aprovador_real_w		spa_aprovacao.nm_aprovador_real%type;

C01 CURSOR FOR
SELECT  nr_sequencia,
	coalesce(nm_aprovador_real,nm_usuario)
from   	spa_aprovacao
where  	nr_seq_spa         	= nr_seq_spa_p
and    	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '')
and    	(dt_lib_aprov IS NOT NULL AND dt_lib_aprov::text <> '')
order by nr_sequencia desc;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_aprovacao_w,
	nm_aprovador_real_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nm_aprovador_real_w = nm_usuario_p) then
		ie_ultima_aprov_w := 'S';
	end if;
	exit;
	end;
end loop;
close C01;

return  ie_ultima_aprov_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
