SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ultimo_atend_cih ( cd_pessoa_fisica_p text, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


nr_atendimento_w	bigint;
nr_atend_isol_w		bigint;
qt_dias_isolamento_w	bigint;

qt_isolamento_w		bigint;

c01 CURSOR FOR
	SELECT	nr_atendimento
	from	atend_paciente_hist
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;


BEGIN

qt_dias_isolamento_w := obter_param_usuario(916, 133, obter_perfil_ativo, nm_usuario_p, 0, qt_dias_isolamento_w);


if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	open c01;
	loop
	fetch c01 into
		nr_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select 	max(nr_atendimento)
		into STRICT	qt_isolamento_w
		from	atend_paciente_hist
		where	ie_evento = 'I'
		and		nr_atendimento	= nr_atendimento_w
		and (clock_timestamp() - dt_inicial ) <= qt_dias_isolamento_w
		and		coalesce(dt_inativacao::text, '') = '';


		if (qt_isolamento_w > 0) then

			nr_atend_isol_w := nr_atendimento_w;

		end if;


		end;
	end loop;
	close c01;



end if;


return	nr_atend_isol_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
