SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ultimo_atend_cirurgia ( CD_PESSOA_FISICA_P text, CD_ESTABELECIMENTO_P bigint, IE_OPCAO_P text) RETURNS bigint AS $body$
DECLARE

 
/* ie_opcao_p 
	'I' /'S'- Independente de estabelecimento 
	'E' - Depende do estabelecimento 
*/
 
nr_atendimento_w	bigint := 0;


BEGIN 
 
if (ie_opcao_p in ('I','S')) then 
	select	max(a.nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from	atendimento_paciente a, 
		cirurgia b 
	where	a.nr_atendimento	= b.nr_atendimento 
	and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p;
elsif (ie_opcao_p = 'E') then 
	select	max(a.nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from	atendimento_paciente a, 
		cirurgia b 
	where	a.nr_atendimento	= b.nr_atendimento 
	and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	a.cd_estabelecimento	= cd_estabelecimento_p;
elsif (ie_opcao_p = 'UC') then 
	select	max(a.nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from	atendimento_paciente a 
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	a.cd_estabelecimento	= cd_estabelecimento_p;
end if;
 
return	nr_atendimento_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
