CREATE OR REPLACE FUNCTION obter_ultimo_atend_paciente (cd_pessoa_fisica_p text, nr_seq_dispositivo_p bigint, dt_atendimento timestamp) RETURNS bigint AS $body$
DECLARE

 
ds_retorno_w	bigint;


BEGIN 
 
 if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '' AND dt_atendimento IS NOT NULL AND dt_atendimento::text <> '') then 
  begin 
  
	select	max(a.nr_atendimento) 
	into STRICT	ds_retorno_w	 
	from	atendimento_paciente a, 
			atend_pac_dispositivo b 
	where	b.nr_atendimento 		= a.nr_atendimento 
	and		(a.dt_alta IS NOT NULL AND a.dt_alta::text <> '')	  
	and		coalesce(b.dt_retirada::text, '') = '' 
	and 	a.cd_pessoa_fisica		= cd_pessoa_fisica_p 
	and		a.cd_estabelecimento 	= coalesce(wheb_usuario_pck.get_cd_estabelecimento, 1) 
	and 	b.nr_sequencia 			= nr_seq_dispositivo_p 
	and 	exists (	SELECT 1 
						from  dispositivo x 
						where  x.nr_sequencia = b.nr_seq_dispositivo 
						and   coalesce(x.ie_permite_disp_alta,'N') = 'S') 
	--and obter_se_atendimento_disp(a.nr_atendimento) = 'S' -- Removido e adicionado a restrição "and b.dt_retirada is null" ao SQL para ganho de performance 
	and 	b.dt_retirada_prev > dt_atendimento;
 
	end;
	end if;
 
	return	ds_retorno_w;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
