CREATE OR REPLACE FUNCTION obter_ultimo_atend_unid_data ( nr_seq_interno_p bigint, dt_referencia_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* ie_opcao_p: 
'NA'	= NÃºmero do Atendimento 
'NPA'	= Nome do Paciente do Atendimento*/
 
 
nr_atendimento_w	unidade_atend_hist.nr_atendimento%type;


BEGIN 
 
select	a.nr_atendimento 
into STRICT	nr_atendimento_w 
from	unidade_atend_hist a 
where	a.nr_sequencia = (	SELECT	coalesce(max(b.nr_sequencia),0) 
				from  unidade_atend_hist b 
				where  b.nr_seq_unidade = nr_seq_interno_p 
				and	b.dt_historico <= dt_referencia_p 
				and	b.ie_status_unidade = 'P');
 
if (ie_opcao_p	= 'NA') then 
	return	nr_atendimento_w;
elsif (ie_opcao_p	= 'NPA') then 
	return	obter_pessoa_atendimento(nr_atendimento_w, 'N');
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
