CREATE OR REPLACE FUNCTION obter_ultimo_catalogo_cid (cd_doenca_cid_p text) RETURNS varchar AS $body$
DECLARE

    ds_retorno_w  varchar(40);
    nr_seq_cid_w cid_doenca_versao.nr_sequencia%type;

BEGIN
    select Max(nr_sequencia)
    into STRICT   nr_seq_cid_w
    from   cid_doenca_versao
    where  cd_doenca_cid like cd_doenca_cid_p
    and    nr_sequencia not in (SELECT Max(nr_sequencia)
                                from   cid_doenca_versao
                                where  cd_doenca_cid like cd_doenca_cid_p);

    select cd_versao
    into STRICT   ds_retorno_w
    from   cid_doenca_versao
    where  nr_sequencia = nr_seq_cid_w;

    return ds_retorno_w;
end;
