SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ultimo_cid_atend (nr_atendimento_p bigint, ie_classificacao_p text, ie_por_usuario_logado bigint default 0) RETURNS varchar AS $body$
DECLARE


/* classificação do cid	 P-Principal S-Secundário
ie_por_usuario_logado  1 true or 0 false  */
cd_doenca_w			varchar(10);
dt_diagnostico_w		timestamp;



BEGIN
cd_doenca_w	:= '';
/* busca diagnostico medico */

begin

select	max(dt_diagnostico)
into STRICT	dt_diagnostico_w
from	diagnostico_medico a
where	a.nr_atendimento	= nr_atendimento_p
and	exists	(	SELECT	1
			from	diagnostico_doenca b
			where	b.nr_atendimento	= a.nr_atendimento
			and	b.dt_diagnostico	= a.dt_diagnostico
			and	b.ie_classificacao_doenca	= ie_classificacao_p
			and     (	(ie_por_usuario_logado = 0) or (ie_por_usuario_logado = 1 and nm_usuario = Obter_Usuario_Ativo)
				)
		);


select	cd_doenca
into STRICT	cd_doenca_w
from	diagnostico_doenca
where	nr_atendimento		= nr_atendimento_p
and	dt_diagnostico		= dt_diagnostico_w
and	ie_classificacao_doenca	= ie_classificacao_p
and     ((ie_por_usuario_logado = 0) or (ie_por_usuario_logado = 1 and nm_usuario = Obter_Usuario_Ativo)
	);
exception
	when others then
	cd_doenca_w	:= '';
end;

RETURN cd_doenca_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
