CREATE OR REPLACE FUNCTION obter_ultimo_codigo_sus ( nr_proc_interno_p bigint) RETURNS bigint AS $body$
DECLARE

 
cd_procedimento_tuss_w	bigint;
cd_convenio_sus_w	integer;

 

BEGIN 
	 
if (coalesce(nr_proc_interno_p,0) > 0) then 
	 
	select	coalesce(max(cd_convenio_sus),0) 
	into STRICT	cd_convenio_sus_w 
	from	parametro_faturamento;
 
	select	coalesce(max(cd_procedimento),0) 
	into STRICT	cd_procedimento_tuss_w 
	from	proc_interno_conv 
	where	nr_seq_proc_interno = nr_proc_interno_p 
	and	cd_convenio = cd_convenio_sus_w;
 
end if;
 
return cd_procedimento_tuss_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
