SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ultimo_evento_pepo ( nr_cirurgia_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_evento_w	varchar(255) := '';

C01 CURSOR FOR
	SELECT obter_desc_evento_cirurgia(x.nr_seq_evento) ds_evento
	from evento_cirurgia_paciente x
	where x.nr_cirurgia = nr_cirurgia_p
	and coalesce(x.dt_inativacao::text, '') = ''
	and (x.dt_liberacao IS NOT NULL AND x.dt_liberacao::text <> '')
	order by dt_atualizacao, campo_numerico(obter_dados_evento_cirurgia(x.nr_seq_evento,'SE'));

BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	begin
		for c01_w in C01 loop
				begin
					ds_evento_w := substr(c01_w.ds_evento,0,255);
				end;
		end loop;	
	end;	
end if;

return	ds_evento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_ultimo_evento_pepo ( nr_cirurgia_p bigint ) FROM PUBLIC;

