CREATE OR REPLACE FUNCTION obter_ultimo_evento_pertence (nr_sequencia_p bigint, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE

/*
cd evento = 'CD'
setor atendimento	= 'SA'


*/
ds_retorno_w		varchar(10);
nr_sequencia_w		bigint;
cd_tipo_evento_w		varchar(10);
cd_setor_atendimento_w	bigint;

BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	pertence_pac_item_evento
where	nr_seq_pert_pac = nr_sequencia_p;


select	coalesce(max(cd_tipo_evento),'X'),
	coalesce(max(cd_setor_atendimento),0)
into STRICT	cd_tipo_evento_w,
	cd_setor_atendimento_w
from	pertence_pac_item_evento
where	nr_sequencia = nr_sequencia_w;

if (ie_tipo_retorno_p = 'CD') then
	ds_retorno_w	:=	cd_tipo_evento_w;
elsif (ie_tipo_retorno_p = 'SA') then
	ds_retorno_w	:=	cd_setor_atendimento_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
