CREATE OR REPLACE FUNCTION obter_ultimo_hist_pend ( nr_seq_pend_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);


BEGIN

select	substr(ds_historico,1,255)
into STRICT	ds_retorno_w
from	CTA_PENDENCIA_HIST
where	nr_seq_pend	= nr_seq_pend_p
and	nr_sequencia = (	SELECT	max(nr_sequencia)
				from 	CTA_PENDENCIA_HIST
				where 	nr_seq_pend	= nr_seq_pend_p);

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
