CREATE OR REPLACE FUNCTION obter_ultimo_horario_solucao (nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_horario_w		varchar(200) := '';
ds_horarios_aux_w	varchar(200) := '';


BEGIN

select ds_horarios
into STRICT   ds_horario_w
from   prescr_solucao
where  nr_prescricao  = nr_prescricao_p
and	   nr_seq_solucao = nr_seq_solucao_p;

if (position(':' in ds_horario_w) > 0 ) then
	ds_horarios_aux_w := substr(ds_horario_w,length(trim(both replace(ds_horario_w,';',' '))) - 5, 6);
else
	ds_horarios_aux_w := substr(ds_horario_w,length(trim(both replace(ds_horario_w,';',' '))) - 2, 3);
end if;

return	ds_horarios_aux_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
