SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ultimo_hor_adm_medic (nr_prescricao_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


dt_horario_w		varchar(255);
nr_atendimento_w	bigint;
dt_fim_w			timestamp;
dt_inicio_w			timestamp;

BEGIN

select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then
	dt_inicio_w	:= trunc(clock_timestamp() - interval '2 days');
	dt_fim_w	:= fim_dia(clock_timestamp() + interval '5 days');
	
	
			
	for	i in 1..8 loop
			
		select	to_char(max(a.dt_horario),'dd/mm/yyyy hh24:mi:ss')
		into STRICT	dt_horario_w
		from   	prescr_medica b,
				prescr_mat_hor a		
		where  	a.nr_prescricao	 = b.nr_prescricao
		and    	b.nr_atendimento = nr_atendimento_w
		and		a.cd_material 	 = cd_material_p
		AND		b.dt_inicio_prescr between dt_inicio_w and dt_fim_w
		and		(a.dt_lib_horario IS NOT NULL AND a.dt_lib_horario::text <> '')
		and		coalesce(a.dt_suspensao::text, '') = ''
		and		(a.dt_fim_horario IS NOT NULL AND a.dt_fim_horario::text <> '');
	
		dt_inicio_w	:= dt_inicio_w - 2;
		dt_fim_w    := dt_fim_w - 2;
		
		if (dt_horario_w IS NOT NULL AND dt_horario_w::text <> '') then
		     exit;
		end if;
		
	end loop;
		
end if;	

return dt_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
