CREATE OR REPLACE FUNCTION obter_ultimo_laudo_apac ( nr_atendimento_p bigint, nr_seq_interno_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w		varchar(1) 	:= 'N';
cd_pessoa_fisica_w	varchar(10);	
 

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	select	cd_pessoa_fisica 
	into STRICT	cd_pessoa_fisica_w 
	from 	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	if (ie_tipo_p = 1) then 
	 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_retorno_w 
		from	sus_laudo_paciente c, 
			atendimento_paciente d 
		where	c.nr_atendimento = d.nr_atendimento 
		and	d.cd_pessoa_fisica = cd_pessoa_fisica_w 
		and	nr_seq_interno_p = (	SELECT	max(a.nr_seq_interno) 
						from	sus_laudo_paciente a, 
							atendimento_paciente b 
						where	a.nr_atendimento = b.nr_atendimento 
						and	b.cd_pessoa_fisica = cd_pessoa_fisica_w);
						 
	elsif (ie_tipo_p = 2) then 
	 
		select	coalesce(max('S'),'N') 
		into STRICT	ie_retorno_w 
		from	sus_laudo_paciente c, 
			atendimento_paciente d 
		where	c.nr_atendimento = d.nr_atendimento 
		and	d.cd_pessoa_fisica = cd_pessoa_fisica_w 
		and	ie_classificacao <> 1 
		and	nr_seq_interno_p = (	SELECT	max(a.nr_seq_interno) 
						from	sus_laudo_paciente a, 
							atendimento_paciente b 
						where	a.nr_atendimento = b.nr_atendimento 
						and	b.cd_pessoa_fisica = cd_pessoa_fisica_w 
						and	ie_classificacao <> 1);
								 
	end if;
	 
end if;
 
return ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_ultimo_laudo_apac ( nr_atendimento_p bigint, nr_seq_interno_p bigint, ie_tipo_p text) FROM PUBLIC;

