CREATE OR REPLACE FUNCTION obter_ultimo_medico_ref_atend (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


cd_medico_referido_w	atendimento_paciente.cd_medico_referido%type;
nr_atendimento_w	atendimento_paciente.nr_atendimento%type;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	coalesce(max(nr_atendimento),0)
	into STRICT	nr_atendimento_w
	from	atendimento_paciente
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	(cd_medico_referido IS NOT NULL AND cd_medico_referido::text <> '');

	if (nr_atendimento_w > 0) then

		select	max(cd_medico_referido)
		into STRICT	cd_medico_referido_w
		from	atendimento_paciente
	        where	nr_atendimento = nr_atendimento_w;

	end if;

end if;

return	cd_medico_referido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
