CREATE OR REPLACE FUNCTION obter_ultimo_mes_migracao (dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE



dt_inicial_w	timestamp;
dt_final_w		timestamp;
qt_dias_w		bigint := 0;
ds_retorno_w	varchar(1) := 'N';


BEGIN

if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') then
	dt_inicial_w	:= trunc(dt_final_p);
	dt_final_w		:= trunc(clock_timestamp());

	qt_dias_w	:= round(dt_inicial_w - dt_final_w);

	if	(qt_dias_w > 0 AND qt_dias_w <= 30) then
		ds_retorno_w := 'S';
	elsif (qt_dias_w < 0) then
		ds_retorno_w := 'N';
	end if;
else
	ds_retorno_w	:= 'X';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
