CREATE OR REPLACE FUNCTION obter_ultimo_motivo_fim_trat (cd_pessoa_fisica_p text, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);	
dt_retorno_w	timestamp;
			

BEGIN
ds_retorno_w	:= '';

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select max(substr(obter_dados_motivo_fim(nr_seq_motivo_fim,'D'),1,255)),
			max(DT_FINAL_TRATAMENTO)
	into STRICT  ds_retorno_w,
		  dt_retorno_w
	from (SELECT nr_seq_motivo_fim,
					dt_final_tratamento
			from  	paciente_tratamento p
			where 	p.cd_pessoa_fisica = cd_pessoa_fisica_p
			and		not exists (select 1 from paciente_tratamento x where x.cd_pessoa_fisica = p.cd_pessoa_Fisica and coalesce(x.dt_final_tratamento::text, '') = '')			
			order by dt_final_tratamento desc) alias7 LIMIT 1;	

end if;

if (ie_retorno_p = 'M') then
	return	ds_retorno_w;
else
	return	dt_retorno_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
