CREATE OR REPLACE FUNCTION obter_ultimo_resp_macroscopia ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(20);
cd_profissional_w varchar(20);

C01 CURSOR FOR
	SELECT a.cd_profissional
	from prescr_proc_macroscopia a,
	      prescr_proc_peca b
	where b.nr_prescricao = nr_prescricao_p
	and   b.nr_sequencia = a.nr_seq_peca
	order by a.dt_atualizacao;

BEGIN
open C01;
loop
fetch C01 into
	cd_profissional_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w := cd_profissional_w;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
