CREATE OR REPLACE FUNCTION obter_ultimo_serv_leito_aberto (nr_seq_unidade_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w	bigint;


BEGIN

select	coalesce(max(a.nr_sequencia),0)
into STRICT	nr_sequencia_w
from	sl_unid_atend a,
	unidade_atendimento b
where	a.nr_seq_unidade = b.nr_seq_interno
and	b.nr_seq_interno = nr_seq_unidade_p
and	(a.dt_inicio IS NOT NULL AND a.dt_inicio::text <> '')
and	coalesce(a.dt_fim::text, '') = '';

return	nr_sequencia_w;

end;
