CREATE OR REPLACE FUNCTION obter_ultimo_setor_int_propaci (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_atepacu_w	procedimento_paciente.nr_seq_atepacu%type;
nr_atendimento_w	procedimento_paciente.nr_atendimento%type;
cd_setor_atendimento_w	setor_atendimento.cd_setor_atendimento%type;


BEGIN

/*Busca a sequÃªncia interno do atendimento_pac_unidade em que o procedimento foi executado*/

select	max(nr_seq_atepacu),
	max(nr_atendimento)
into STRICT	nr_seq_atepacu_w,
	nr_atendimento_w
from	procedimento_paciente
where	nr_sequencia		= nr_sequencia_p;

select	max(a.cd_setor_atendimento)
into STRICT	cd_setor_atendimento_w
from	atend_paciente_unidade a,
	setor_atendimento b
where	b.cd_setor_atendimento	= a.cd_setor_atendimento
and	b.cd_classif_setor 	in (3,4,8)
and	a.nr_seq_interno	< nr_seq_atepacu_w
and	a.nr_atendimento	= nr_atendimento_w;

return	cd_setor_atendimento_w;

end;
