CREATE OR REPLACE FUNCTION obter_ultimo_turno_prescr (nr_prescricao_p bigint, cd_local_estoque_p bigint) RETURNS varchar AS $body$
DECLARE


ds_turno_w			varchar(100) := null;


BEGIN

select  max(a.ds_turno)
into STRICT    ds_turno_w
from    regra_turno_disp a
where   a.nr_sequencia =
    (SELECT max(x.nr_seq_turno)
    from    prescr_mat_hor x
    where   x.nr_seq_turno = x.nr_sequencia
    and x.nr_prescricao = nr_prescricao_p
    and (x.dt_emissao_farmacia IS NOT NULL AND x.dt_emissao_farmacia::text <> '')
    and Obter_se_horario_liberado(x.dt_lib_horario, x.dt_horario) = 'S'
    and x.cd_local_estoque = cd_local_estoque_p);

return	ds_turno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
