CREATE OR REPLACE FUNCTION obter_ultimo_usuario_troca (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE




nm_usuario_troca_w		varchar(255);
nr_sequencia_w			bigint;

BEGIN


if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	select max(nr_sequencia)
	into STRICT nr_sequencia_w
	from historico_localizacao_pa
	where nr_atendimento = nr_atendimento_p;

	select nm_usuario
	into STRICT nm_usuario_troca_w
	from historico_localizacao_pa
	where nr_sequencia = nr_sequencia_w;

end if;

return	nm_usuario_troca_w;

end;
