CREATE OR REPLACE FUNCTION obter_ult_acesso_tasy ( nm_usuario_p text) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp;


BEGIN
select	max(a.dt_acesso)
into STRICT	dt_retorno_w
from	tasy_log_acesso a
where	a.nr_sequencia = (	SELECT	max(x.nr_sequencia)
			from	tasy_log_acesso x
			where	x.nm_usuario = nm_usuario_p
			and	x.ie_result_acesso = 'N'
			and	dt_acesso > clock_timestamp() - interval '60 days');

return	dt_retorno_w;

end;
