CREATE OR REPLACE FUNCTION obter_ult_atends_sem_atual ( cd_pessoa_fisica_p text, qt_atendimento_p bigint, ie_cid_p text, nr_atendimento_atual_p bigint) RETURNS varchar AS $body$
DECLARE

/* 
ie_cid_p = 'S'	Retorna o CID 
ie_cid_p = 'N'	Não retorna o CID 
ie_cid_p = 'L'	Retorna o CID mas não quebra linha 
*/
 
nr_atendimento_w	bigint;
dt_entrada_w		timestamp;
ds_retorno_w		varchar(2000) := '';
cd_doenca_w		varchar(10);
C01 CURSOR FOR
	SELECT * 
	FROM (	SELECT	a.nr_atendimento, 
 			a.dt_entrada, 
			SUBSTR(obter_cid_atendimento(a.nr_atendimento,'P'),1,10) 
		FROM	atendimento_paciente	a 
		WHERE	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
		AND	a.nr_atendimento <> nr_atendimento_atual_p 
		ORDER BY a.nr_atendimento DESC) alias2 LIMIT (qt_atendimento_p);

BEGIN
open c01;
loop 
fetch	c01 into 
	nr_atendimento_w, 
 	dt_entrada_w, 
	cd_doenca_w;
 	EXIT WHEN NOT FOUND; /* apply on c01 */
 		if (ie_cid_p = 'S') then 
			ds_retorno_w := ds_retorno_w	|| LPAD(TO_CHAR(nr_atendimento_w),15,' ')	 
							|| LPAD(TO_CHAR(dt_entrada_w),30, ' ')		 
							|| LPAD(cd_doenca_w,25, ' ') || chr(13); /* Traz o CID com quebra de linha*/
		elsif (ie_cid_p = 'N') then 
			ds_retorno_w := ds_retorno_w	|| LPAD(TO_CHAR(nr_atendimento_w),14,' ') 
							||' - ' || LPAD(TO_CHAR(dt_entrada_w),8, ' ');
		elsif (ie_cid_p = 'L') then 
			ds_retorno_w := ds_retorno_w	|| LPAD(TO_CHAR(nr_atendimento_w),10,' ') /* Traz o CID sem quebra de linha*/
	 
							|| LPAD(TO_CHAR(dt_entrada_w),10, ' ')		 
							|| LPAD(cd_doenca_w,10, ' ') 
							|| LPAD('/',5, ' ');
		end if;
 	end loop;
close C01;
return ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
