CREATE OR REPLACE FUNCTION obter_ult_atend_unidade (cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, ie_consistir_hist_p text default null) RETURNS bigint AS $body$
DECLARE


nr_atendimento_w	bigint;
nr_seq_interno_w	bigint;
nr_sequencia_w		bigint;


BEGIN

select 	max(nr_atendimento),
	max(nr_seq_interno)
into STRICT	nr_atendimento_w,
	nr_seq_interno_w
from 	unidade_atendimento
where 	cd_setor_atendimento 	= cd_setor_atendimento_p
  and 	cd_unidade_basica 	= cd_unidade_basica_p
  and 	cd_unidade_compl  	= cd_unidade_compl_p;


if (coalesce(nr_atendimento_w::text, '') = '')  and (coalesce(ie_consistir_hist_p,'S') = 'S') then

	select 	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from 	unidade_atend_hist
	where 	ie_status_unidade = 'P'
	and 	nr_seq_unidade = nr_seq_interno_w;

	select 	max(nr_atendimento)
	into STRICT 	nr_atendimento_w
	from 	unidade_atend_hist
	where 	nr_sequencia = nr_sequencia_w;

end if;

return	nr_atendimento_w;

end;
