SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ult_cirurgia_graf ( nr_atendimento_p bigint, ie_param_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


nr_cirurgia_retorno_w 	bigint;


BEGIN

if (ie_param_p = 'G') then

	Select 	max(a.nr_cirurgia)
	into STRICT	nr_cirurgia_retorno_w
	from   	pepo_sv_cirurgia a,
			cirurgia c
	where  	(a.nr_cirurgia IS NOT NULL AND a.nr_cirurgia::text <> '')
	and    	c.nr_cirurgia = a.nr_cirurgia
	and    	c.nr_atendimento = nr_atendimento_p
	and    	a.nm_usuario = nm_usuario_p;

else

	Select 	max(a.nr_cirurgia)
	into STRICT	nr_cirurgia_retorno_w
	from 	pepo_item_grafico a,
			cirurgia_agente_anestesico b,
			cirurgia d
	where 	a.nr_seq_cirur_agente = b.nr_sequencia
	 and	(a.nr_cirurgia IS NOT NULL AND a.nr_cirurgia::text <> '')
	 and    d.nr_cirurgia = a.nr_cirurgia
	 and    d.nr_atendimento = nr_atendimento_p
	 and 	(a.nr_seq_cirur_agente IS NOT NULL AND a.nr_seq_cirur_agente::text <> '')
	 and    	a.nm_usuario = nm_usuario_p
	 and	a.ie_grafico = 'S'
	 and 	coalesce(b.ie_situacao,'A') = 'A';

end if;


return	nr_cirurgia_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
