CREATE OR REPLACE FUNCTION obter_ult_custo_medio_mat ( cd_estabelecimento_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


vl_custo_medio_w double precision := 0;
cd_material_w integer;

C01 CURSOR FOR
	SELECT	coalesce(vl_custo_medio,0)
	from	saldo_estoque
	where	cd_material		= cd_material_w
	and	cd_estabelecimento		= coalesce(cd_estabelecimento_p, cd_estabelecimento)
	and	coalesce(vl_custo_medio,0) <> 0
	order by dt_mesano_referencia asc,
		vl_custo_medio asc;

BEGIN
select	coalesce(cd_material_estoque, cd_material)
into STRICT	cd_material_w
from	material
where	cd_material = cd_material_p;

open C01;
loop
fetch C01 into
	vl_custo_medio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return vl_custo_medio_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
