SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ult_dados_cid_loco_reg (cd_pessoa_fisica_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p		C    - Código
			D   - Descricao
			CD - Código + Descrição
			E    - Estadiamento
			DT  - Descrição Tipo diagnóstico
					*/
nr_sequencia_w	bigint;
cd_cid_w	varchar(10);
ds_cid_w	varchar(200);
ds_estadio_w 	varchar(20);
ds_tipo_diagnostico_w	varchar(255);


BEGIN

Select 	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	can_loco_regional
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then

	select	upper(cd_doenca_cid),
		substr(obter_desc_cid(cd_doenca_cid),1,200),
		cd_estadiamento,
		obter_valor_dominio(13, ie_tipo_diagnostico)
	into STRICT	cd_cid_w,
		ds_cid_w,
		ds_estadio_w,
		ds_tipo_diagnostico_w
	from	can_loco_regional
	where	nr_sequencia = nr_sequencia_w;
end if;

if (ie_opcao_p = 'C') then
	return 	cd_cid_w;
elsif (ie_opcao_p = 'D') then
	return 	ds_cid_w;
elsif (ie_opcao_p = 'CD') then
	return 	cd_cid_w ||' - '||ds_cid_w;
elsif (ie_opcao_p = 'E') then
	return	ds_estadio_w;
elsif (ie_opcao_p = 'DT') then
	return	ds_tipo_diagnostico_w;
end if;

exception
	when others then
	return '';


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
