CREATE OR REPLACE FUNCTION obter_ult_data_liq (nr_sequencia_p bigint) RETURNS timestamp AS $body$
DECLARE

ds_retorno_w	timestamp;


BEGIN

select  distinct
        max(a.dt_liquidacao)
into STRICT 	ds_retorno_w
from 	titulo_pagar a,
	nota_fiscal b
where 	a.nr_seq_nota_fiscal = b.nr_sequencia
and	b.nr_sequencia = nr_sequencia_p
and	(a.dt_liquidacao IS NOT NULL AND a.dt_liquidacao::text <> '')
order by 1;



return	ds_retorno_w;

end;
$body$
