CREATE OR REPLACE FUNCTION obter_ult_estagio_lote_rec (nr_seq_lote_rec_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_estagio_w		bigint;
ds_retorno_w		varchar(80);
ds_estagio_lote_w	varchar(80);
qt_dias_w		integer;
nr_seq_est_lote_rec_w	bigint;
qt_dias_previsto_w	bigint;


BEGIN

ds_retorno_w	:= null;

select 	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_estagio_w
from 	lote_audit_rec_estagio
where	nr_seq_lote_audit_rec  = nr_seq_lote_rec_p;

if (nr_seq_estagio_w > 0) then


	select 	ds_estagio_lote,
		trunc(clock_timestamp() - dt_estagio) qt_dias,
		b.nr_sequencia,
		qt_dias_previsto
	into STRICT	ds_estagio_lote_w,
		qt_dias_w,
		nr_seq_est_lote_rec_w,
		qt_dias_previsto_w
	from 	lote_audit_rec_estagio a,
		estagio_lote_audit_rec b
	where 	b.nr_sequencia = a.nr_seq_est_lote_rec
	and	a.nr_sequencia = nr_seq_estagio_w;

end if;

if (ie_opcao_p = 0) then
	ds_retorno_w := ds_estagio_lote_w;
elsif (ie_opcao_p = 1) then
	ds_retorno_w := qt_dias_w;
elsif (ie_opcao_p = 2) then
	ds_retorno_w := nr_seq_est_lote_rec_w;
elsif (ie_opcao_p = 3) then
	ds_retorno_w := qt_dias_previsto_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
