	if (coalesce(nr_seq_evento_w,0) > 0) then

		select	max(obter_desc_evento_qua(nr_seq_evento))
		into STRICT	ds_retorno_W
		from	qua_evento_paciente
		where	nr_sequencia = nr_seq_evento_w;
	end if;

end if;

return	ds_retorno_w;

end;
