CREATE OR REPLACE FUNCTION obter_ult_evento_sne ( nr_prescricao_p bigint, nr_seq_material_p bigint) RETURNS bigint AS $body$
DECLARE


ie_alteracao_w		integer;
nr_sequencia_w		bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	prescr_solucao_evento
where	ie_tipo_solucao = 2
and	nr_prescricao = nr_prescricao_p
and	nr_seq_material = nr_seq_material_p
and	ie_alteracao <> 5
and	ie_evento_valido = 'S';

if (nr_sequencia_w > 0) then

	select	ie_alteracao
	into STRICT	ie_alteracao_w
	from	prescr_solucao_evento
	where	nr_sequencia = nr_sequencia_w
	and	ie_alteracao not in (5);

end if;

return	ie_alteracao_w;

end;
