CREATE OR REPLACE FUNCTION obter_ult_horario_sol (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_horario_w		varchar(50);
nr_prescricao_w		bigint;
nr_seq_solucao_w	bigint;


BEGIN

select	max(a.nr_prescricao)
into STRICT	nr_prescricao_w
from	prescr_solucao a,
	prescr_medica b
where	a.nr_prescricao		= b.nr_prescricao
and	b.nr_atendimento	= nr_atendimento_p
and	b.dt_inicio_prescr 	between clock_timestamp() - interval '15 days' and clock_timestamp() + interval '1 days'
and	(a.ds_horarios IS NOT NULL AND a.ds_horarios::text <> '');

if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then
	select	max(a.nr_seq_solucao)
	into STRICT	nr_seq_solucao_w
	from	prescr_solucao a,
		prescr_medica b
	where	a.nr_prescricao		= b.nr_prescricao
	and	a.nr_prescricao		= nr_prescricao_w
	and	(a.ds_horarios IS NOT NULL AND a.ds_horarios::text <> '');

	select	trim(both max(obter_ultimo_horario_solucao(nr_prescricao_w,nr_seq_solucao_w)))
	into STRICT	ds_horario_w
	;

end if;

if (ds_horario_w IS NOT NULL AND ds_horario_w::text <> '') then
	if (length(ds_horario_w) = 2) then
		ds_horario_w	:= ds_horario_w || ':00';
	end if;
end if;

return	ds_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
