CREATE OR REPLACE FUNCTION obter_ult_motivo_isolamento (nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_motivo_w			bigint;
ds_motivo_w			varchar(100);
nr_seq_motivo_atend_w		bigint;
nr_seq_motivo_isol_w		bigint;
dt_inicio_isolamento_w		timestamp;


BEGIN

select	coalesce(max(nr_Sequencia),0)
into STRICT	nr_seq_motivo_atend_w
from	motivo_isolamento_atend
where	nr_atendimento = nr_atendimento_p;

if (nr_seq_motivo_atend_w > 0) then
	select  nr_seq_motivo_isol,
		dt_inicio_isolamento
	into STRICT	nr_seq_motivo_isol_w,
		dt_inicio_isolamento_w
	from	motivo_isolamento_atend
	where	nr_sequencia = nr_seq_motivo_atend_w;

	select  ds_motivo
	into STRICT	ds_motivo_w
	from	motivo_isolamento
	where	nr_sequencia = nr_seq_motivo_isol_w;
end if;

if (upper(ie_opcao_p) = 'C') then
	return	nr_seq_motivo_isol_w;
elsif (upper(ie_opcao_p) = 'I') then
	return	to_char(dt_inicio_isolamento_w,'dd/mm/yyyy hh24:mi:ss');
else
	return ds_motivo_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
