CREATE OR REPLACE FUNCTION obter_ult_mot_hist (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nr_seq_aux_w	bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nr_sequencia_p > 0) then
	begin

	select	max(b.nr_sequencia)
	into STRICT	nr_seq_aux_w
	from	gestao_vaga_hist b
	where	b.nr_seq_gestao = nr_sequencia_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

	if (nr_seq_aux_w > 0) then
		begin

			select	substr(obter_descricao_padrao('GESTAO_VAGA_MOTIVO_HIST','DS_MOTIVO',NR_SEQ_MOTIVO_HISTORICO),1,255)
			into STRICT	ds_retorno_w
			from	gestao_vaga_hist a
			where	a.nr_sequencia = nr_seq_aux_w;

		end;
	end if;

	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
