CREATE OR REPLACE FUNCTION obter_ult_nota_mat ( cd_material_p bigint, cd_estabelecimento_p bigint, cd_local_estoque_p bigint) RETURNS bigint AS $body$
DECLARE


	ds_retorno_w		bigint;


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	select a.nr_sequencia
	into STRICT   ds_retorno_w
	from   nota_fiscal a,
	       nota_fiscal_item b
	where  a.nr_sequencia = b.nr_sequencia
	and    b.cd_material = cd_material_p
	and    a.ie_situacao  = '1'
	and    a.ie_tipo_nota in ('EN','EF')
	and	((cd_local_estoque_p = 0) or (b.cd_local_estoque = cd_local_estoque_p))
	and    (a.dt_atualizacao_estoque IS NOT NULL AND a.dt_atualizacao_estoque::text <> '')
	and	a.cd_estabelecimento = cd_estabelecimento_p
	and    a.dt_atualizacao_estoque = (SELECT max(dt_atualizacao_estoque) from nota_fiscal);



end if;

RETURN ds_retorno_w;

END;
