CREATE OR REPLACE FUNCTION obter_ult_oc_material ( cd_material_p bigint, cd_estabelecimento_p bigint, cd_local_estoque_p bigint) RETURNS bigint AS $body$
DECLARE

 
ds_retorno_w		bigint;


BEGIN 
 
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then 
	select a.nr_ordem_compra 
	into STRICT 	ds_retorno_w 
	from  ordem_compra a, 
		ordem_compra_item b 
	where  a.nr_ordem_compra = b.nr_ordem_compra 
	and   b.cd_material = cd_material_p 
	and	coalesce(a.nr_seq_motivo_cancel::text, '') = '' 
	and	((cd_local_estoque_p = 0) or (b.cd_local_estoque = cd_local_estoque_p)) 
	and	a.cd_estabelecimento = cd_estabelecimento_p 
	and	a.dt_liberacao = (SELECT max(dt_liberacao) from ordem_compra);
 
end if;
 
RETURN ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
