SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ult_resultado_data_lab (cd_pessoa_fisica_p text, seq_grid_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE

nr_seq_resultado_w		bigint;
ds_resultado_w			varchar(90)	:= '-';
nr_seq_resultado_prescr_w	bigint;
dt_referencia_w			timestamp;			

BEGIN

select 	dt_referencia
into STRICT	dt_referencia_w
from	HD_EXAME_GRID_CONTROLE
where 	nr_sequencia = seq_grid_p;

select 	coalesce(max(c.nr_seq_resultado),0)
into STRICT	nr_seq_resultado_w
from	exame_laboratorio d,
	exame_lab_resultado a,
	exame_lab_result_item c
where	a.nr_seq_resultado 	= c.nr_seq_resultado
and	d.nr_seq_exame 		= c.nr_seq_exame
and	trunc(coalesce(c.dt_digitacao,a.dt_resultado),'month') = trunc(dt_referencia_w,'month')
--and	c.dt_digitacao		<= dt_referencia_p
and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
and	c.nr_seq_exame		= nr_seq_exame_p
and (coalesce(coalesce(CASE WHEN c.ds_resultado='0' THEN ''  ELSE CASE WHEN d.ie_formato_resultado='V' THEN ''  ELSE c.ds_resultado END  END ,	coalesce(to_char(c.qt_resultado),to_char(CASE WHEN c.pr_resultado=0 THEN ''  ELSE c.pr_resultado END ))),c.ds_resultado)) is not null
and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> ''); --35 - Aprovado, 40 - Liberado
select 	coalesce(max(c.nr_seq_resultado),0)
into STRICT	nr_seq_resultado_prescr_w
from	exame_laboratorio d,
	exame_lab_resultado a,
	exame_lab_result_item c,
	prescr_medica b,
	prescr_procedimento e	
where	a.nr_seq_resultado 	= c.nr_seq_resultado
and	d.nr_seq_exame 		= c.nr_seq_exame
and     a.nr_prescricao = e.nr_prescricao
and 	c.nr_seq_prescr = e.nr_sequencia
AND ((c.dt_aprovacao IS NOT NULL AND c.dt_aprovacao::text <> '') OR (EXISTS (SELECT 1 FROM exame_lab_result_item y,
					 			  	 		 		 	exame_lab_resultado x
												WHERE	x.nr_seq_resultado	= y.nr_seq_resultado
												AND	x.nr_prescricao		= a.nr_prescricao
												AND	y.nr_seq_prescr		= c.nr_seq_prescr
												AND (y.dt_aprovacao IS NOT NULL AND y.dt_aprovacao::text <> ''))))
and	trunc(coalesce(c.dt_digitacao,a.dt_resultado),'month') = trunc(dt_referencia_w,'month')
and	b.cd_pessoa_fisica	= cd_pessoa_fisica_p
and	c.nr_seq_exame		= nr_seq_exame_p
and	b.nr_prescricao		= a.nr_prescricao
and (coalesce(coalesce(CASE WHEN c.ds_resultado='0' THEN ''  ELSE CASE WHEN d.ie_formato_resultado='V' THEN ''  ELSE c.ds_resultado END  END ,	coalesce(to_char(c.qt_resultado),to_char(CASE WHEN c.pr_resultado=0 THEN ''  ELSE c.pr_resultado END ))),c.ds_resultado)) is not null
and     e.ie_status_atend >= 35; --35 - Aprovado, 40 - Liberado
if (nr_seq_resultado_prescr_w > nr_seq_resultado_w) then
	nr_seq_resultado_w	:= nr_seq_resultado_prescr_w;
end if;

if (nr_seq_resultado_w > 0) then

	select	substr(max(coalesce(coalesce(CASE WHEN c.ds_resultado='0' THEN ''  ELSE CASE WHEN d.ie_formato_resultado='V' THEN ''  ELSE c.ds_resultado END  END ,	coalesce(to_char(c.qt_resultado),to_char(CASE WHEN c.pr_resultado=0 THEN ''  ELSE c.pr_resultado END ))),c.ds_resultado)),1,90)
	into STRICT	ds_resultado_w
	from	exame_laboratorio d,
		exame_lab_result_item c
	where	c.nr_seq_resultado 	= nr_seq_resultado_w
	and	c.nr_seq_exame		= nr_seq_exame_p
	and	d.nr_seq_exame 		= c.nr_seq_exame;
end if;

return ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_ult_resultado_data_lab (cd_pessoa_fisica_p text, seq_grid_p bigint, nr_seq_exame_p bigint) FROM PUBLIC;

