CREATE OR REPLACE FUNCTION obter_ult_transferencia ( nr_seq_senha_p bigint) RETURNS varchar AS $body$
DECLARE


nm_usuario_w			varchar(255);
nr_seq_mot_trasf_senha_w	bigint;
nr_fila_destino_w		bigint;
ds_motivo_w			varchar(255);
ds_fila_w			varchar(255);
retorno				varchar(255) := '';


BEGIN

select	nm_usuario,
	nr_seq_mot_trasf_senha,
	nr_fila_destino
into STRICT	nm_usuario_w,
	nr_seq_mot_trasf_senha_w,
	nr_fila_destino_w
from	paciente_senha_transf_hist
where	nr_seq_senha = nr_seq_senha_p
and	nr_sequencia = (SELECT max(nr_sequencia)
 				   from paciente_senha_transf_hist
 				   where nr_seq_senha = nr_seq_senha_p);

select	coalesce(ds_curto, ds_fila)
into STRICT	ds_fila_w
from	fila_espera_senha
where	nr_sequencia = nr_fila_destino_w;

if (nr_seq_mot_trasf_senha_w IS NOT NULL AND nr_seq_mot_trasf_senha_w::text <> '') then
	select	ds_motivo
	into STRICT	ds_motivo_w
	from	motivo_transf_senha
	where	nr_sequencia = nr_seq_mot_trasf_senha_w;

	retorno := WHEB_MENSAGEM_PCK.get_texto(784301,'DS_FILA='|| ds_fila_w || ';' || 'NM_USUARIO='|| nm_usuario_w || ';' || 'DS_MOTIVO='|| ds_motivo_w );
--  Esta senha j치 foi transferida para a fila #@DS_FILA#@ pelo usu치rio #@NM_USUARIO#@ com o motivo #@DS_MOTIVO#@
else
	retorno := WHEB_MENSAGEM_PCK.get_texto(784302,'DS_FILA=' || ds_fila_w || ';' ||'NM_USUARIO='|| nm_usuario_w );
-- Esta senha j치 foi transferida para a fila #@DS_FILA#@ pelo usu치rio #@NM_USUARIO#@
end if;

return	retorno;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
